          todo= JSON.parse(localStorage.getItem('todo_table'));

      }




    return (
        <div className="my-4 align-items-center">
           {todos.map(todo => {
            return <TodoItem key={todo.Key} todo={todo}/>
           })}
        </div>
    )

}

export default TodoList
