import { ADD_TODO, DELETE_TODO, UPDATE_TODO} from './actions';
import {todos} from './states'

export let reducer=(state=todos,action) => {
    let newTodos;

    switch(action.type)
    {  
        case ADD_TODO:
                        newTodos=[...state];
                        newTodos.push(action.payload)

                        let tasks=localStorage.getItem("todo_table");
                        if(tasks==null)
                        {
                          var todo=[];
                         }
                        else{
                             todo=JSON.parse(tasks);
                            }

                        todo.push({'Task':action.payload.name,'Key':action.payload.id});
                        localStorage.setItem("todo_table",JSON.stringify(todo));

                        return newTodos;
                        break;

        case DELETE_TODO:
                        newTodos=[...state];
                        newTodos=newTodos.filter(todo=> todo.id !== action.payload);

                        var todo=[];
                        if(localStorage.getItem('task_table')===null){
                            todo=[];
                        } else{
                            todo= JSON.parse(localStorage.getItem('task_table'));

                        }

                        return newTodos;
                        break; 

        case UPDATE_TODO:
                        break;  

        default:
                 return state;
    }
}
